import styled from 'styled-components';

const StartMenu = styled.div`
	margin: auto;
	height: 10rem;
	width: 8rem;
	justify-self: center;
	align-self: center;
	border-radius: 0.5rem;
`;

const StartButton = styled.button`
    background: rgb(220, 190, 95);
	padding: 0.5rem;
	height: 3rem;
	width: 5rem;
	align-self: center;
	margin: auto;
	text-align: center;
	border: 2px solid rgb(80, 65, 25);
	border-radius: 0.5rem;
`;

export { StartMenu, StartButton };
