import styled from "styled-components";

const InputForm = styled.form`
    height: 25px;
	align-self: center;
	margin-top: 25px;
`;

const UserInput = styled.input`
    display: block;
	background: rgb(220, 190, 95);
	border-radius: 0.5rem;
	border: rgb(80, 65, 25);
	height: 25px;
	width: 15rem;
	text-align: center;
	color: rgb(80, 65, 25);
`;

export { InputForm, UserInput };
