import React from 'react';

const startGame = () => {
    menu.style.display = 'none';
    map.style.display = 'grid';
    gameText.style.display = 'flex';
}

class Menu extends React.Component {
    render() {
        return (
            <div className="menu">
                <button id="startButton" onClick={startGame}>Start</button>
            </div>
        )
    }
}

export default Menu;
