import React from 'react';

const textDisplay = {
    hidden: { display: 'none' },
    visible: { display: 'flex' }
}

class GameText extends React.Component {
    render() {
        return (
            <h1 style={this.props.gameStarted ? textDisplay.visible : textDisplay.hidden}>This is the narrator text.</h1>
        )
    }
}

export default GameText;
