import React from 'react';
import Map from './Map';
import Menu from './Menu';
import GameText from './GameText';

class App extends React.Component {
    state = {
        gameStarted: false,
        gameOver: false
    };
    handleClick = () => {
        this.setState({ clicked: true });
    }
    render() {
        return (
            <React.Fragment>
                <Menu handleClick={this.handleClick} />
                <Map />
                <GameText />
            </React.Fragment>
        )
    }
}

export default App;
