import React from 'react';

const textDisplay = {
    hidden: { display: 'none' },
    visible: { display: 'flex' }
}

class GameText extends React.Component {
    render() {
        return (
            <React.Fragment>
                <h1
                    className=""
                    style={this.props.gameStarted ? textDisplay.visible : textDisplay.hidden}>
                    This is the narrator text.
                </h1>
            </React.Fragment>
        )
    }
}

export default GameText;
